  const [isLike, setIsLike] = useState(false);

  const commentInput = useRef();

  // 좋아요 버튼(하트) 클릭시
  const clickLike = (e) => {
    setIsLike(!isLike);
    // axios.post(`http://localhost:3001/board/post/=?/like`);
  };
  const clickGoToCommnet = (e) => {
    commentInput.current.focus();
  };

