const PostUploadForm = () => {
  // const [imgUrl, setImgUrl] = useState("");

  // const encodeFileToBase64 = (fileBlob) => {
  //   const reader = new FileReader();
  //   reader.readAsDataURL(fileBlob);
  //   return new Promise((resolve) => {
  //     reader.onload = () => {
  //       setImgUrl(reader.result);
  //       resolve();
  //     }
  //   })
  // }

  const [imgBase64, setImgBase64] = useState([]);
  const [imgFile, setImgFile] = useState(null);

  const handleChangeFile = (e) => {
    console.log(e.target.files);
    setImgFile(e.target.files);
    // fd.append("file", e.target.files)
    setImgBase64([]);
    for (let i = 0; i < e.target.files.length; i++) {
      let reader = new FileReader();
      reader.readAsDataURL(e.target.files[i]);

      reader.onloadend = () => {
        // 파일 읽기가 완료되면 아래의 코드가 실행
        const base64 = reader.result;
        console.log(base64);
        if (base64) {
          // images.push(base64.toString())
          let base64Sub = base64.toString();

          setImgBase64((imgBase64) => [...imgBase64, base64Sub]);
          // setImgBase64(newObj);
          // 파일 base64 상태 업데이트
          // console.log(images)
        }
      };
    }
  };

  return (
    <>
      <div className="post-upload-form-container">
        <h2 style={{ marginBottom: '30px' }}>일반 게시물 업로드 폼</h2>
        <label htmlFor="post-img-select">이미지 업로드</label>
        <input
          type="file"
          id="post-img-select"
          multiple
          onChange={handleChangeFile}
        />

        {imgBase64.map((img) => {
          return (
            <div className="img-preview-container">
              <img src={img} alt="업로드할 이미지" />
            </div>
          );
        })}

        <textarea placeholder="내용을 입력하세요..."></textarea>
      </div>
    </>
  );
};

const MatePostUploadForm = () => {
  return <div>메이트찾기 게시물 업로드 폼</div>;
};

export {
  PostContainer,
  MiniPostContainer,
  CommentContainer,
  PostBackground,
  MiniMatePostContainer,
  MatePostContainer,
  PostUploadForm,
  MatePostUploadForm,
};
